import React, { useState } from "react";
import validator from 'validator'

const App = () => {

  const [errorMessageCard, setErrorMessage] = useState('')

  const validateCreditCard = (value) => {

    if (validator.isCreditCard(value)) {
      setErrorMessage('✅')
    } else {
      setErrorMessage('❌')
    }
  }

  return (
    <div style={{
      marginLeft: '200px',
    }}>
      <pre>
        <h2>Validate and Save Credit Card below</h2>

        <span>Enter CreditCard: </span>
          <input type="text" onChange={(e)=> validateCreditCard(e.target.value)}></input>
          <span style={{
              fontWeight: 'bold',
              color: 'red',
            }}>{errorMessageCard}
            <br />
        </span>

        <span>Enter Country: </span>
          <input type="text"></input>
          <br />
          <span style={{
              fontWeight: 'bold',
              color: 'red',
            }}>
            <br />
        </span>

        <button id='saveButton' style={{display: 'none'}}>Hello </button>
      </pre>
    </div>
  );
}
export default App
