import React, { useState } from "react";
import validator from 'validator';
import raw from './blacklistedCountries.txt';


const App = () => {

  const [errorMessageCardNumber, setErrorMessage] = useState('')
  const [errorMessageCountry, setCountryErrorMessage] = useState('')

  const [countrydisabled , setCountryDisabled] = useState(true) 
  const [buttondisabled , setButtonDisabled] = useState(true) 

  const validateCreditCard = (value) => {

    if (validator.isCreditCard(value)) {
      setErrorMessage('✅')
      setCountryDisabled(false)

    } else {
      setErrorMessage('❌')
      setCountryDisabled(true) 
    }
  }

  const validateCountry = (value) => {
   fetch(raw).then(r => r.text()).then(text => {
    var blacklistedCountriesArray = text.split('\n');
    for (var i = 0; i < blacklistedCountriesArray.length; i++) {
      blacklistedCountriesArray[i] = blacklistedCountriesArray[i].trim()
    } 
      if (blacklistedCountriesArray.indexOf(value.trim()) === -1 && value !== '') {
        setCountryErrorMessage('✅')
        setButtonDisabled(false)
      } else {
        setCountryErrorMessage('❌')
        setButtonDisabled(true) 
      }
    });
  }

  return (
    <div style={{
      marginLeft: '100px',
    }}>
      <pre>
        <h2>Validate and Save Credit Card below</h2>

          <input type="text" placeholder="Enter Credit Card Number" onChange={(e)=> validateCreditCard(e.target.value)}></input>
          <span>{errorMessageCardNumber}
            <br />
          </span>

          <input disabled={countrydisabled} placeholder="Enter Country" type="text" onChange={(e)=> validateCountry(e.target.value)}></input>
          <span>{errorMessageCountry}
            <br />
          </span>

        <button disabled={buttondisabled} id='saveButton' >Save Information </button>
      </pre>
    </div>
  );
}
export default App
