import React, { useState } from "react";
import validator from 'validator'



const App = () => {

  const [errorMessageCardNumber, setErrorMessage] = useState('')

  const validateCreditCard = (value) => {

    if (validator.isCreditCard(value)) {
      setErrorMessage('✅')
      this.setState({ showStore: true });

    } else {
      setErrorMessage('❌')
    }
  }

  return (
    <div style={{
      marginLeft: '200px',
    }}>
      <pre>
        <h2>Validate and Save Credit Card below</h2>

          <input type="text" placeholder="Enter Credit Card Number" onChange={(e)=> validateCreditCard(e.target.value)}></input>
          <span style={{
              fontWeight: 'bold',
              color: 'red',
            }}>{errorMessageCardNumber}
            <br />
          </span>

          <input placeholder="Enter Country" type="text"></input>
          <br />
          <span style={{
              fontWeight: 'bold',
              color: 'red',
            }}>
            <br />
          </span>

        <button id='saveButton' style={{display: this.state.showStore ? 'block' : 'none' }}>Hello </button>
      </pre>
    </div>
  );
}
export default App
