  }

  const validateCreditCard = (value) => {

    if (validator.isCreditCard(value)) {
      setErrorMessage('✅')
      setCountryDisabled(false)

      var lookup = require('binlookup')()
      lookup('45717360', function( err, data ){
        if (err)
          return console.error(err)

        console.log(data)
      })

    } else {
      setErrorMessage('❌')
      setCountryDisabled(true) 
    }
  }



  return (
    <div style={{
      marginLeft: '200px',
    }}>
      <pre>
        <h2>Validate and Save Credit Card below</h2>

          <input type="text" placeholder="Enter Credit Card Number" onChange={(e)=> validateCreditCard(e.target.value)}></input>
          <span>{errorMessageCardNumber}
            <br />
          </span>

          <input disabled={countrydisabled} placeholder="Enter Country" type="text"></input>
          <br />
          <span>
            <br />
          </span>

        <button disabled={disabled} id='saveButton' >Save Information </button>
      </pre>
    </div>
  );
}
export default App
